import React from 'react';
import "./GeneralSorting.css";
import Block from './utils/Block';
import Generation from './Generation';
import Sorting from './Sorting';

class GeneralSorting extends Block {
    constructor(props) {
        super(props);
        this.state = {
            textareaValue: ""
        };
        this.updateValue = this.updateValue.bind(this);
        this.updateParent = this.updateParent.bind(this);
    }

    updateValue(event) {
        this.setStateParam("textareaValue", event.target.value);
    }

    updateParent(value) {
        var mystate = this.state;
        mystate.textareaValue = value;

        this.setState(mystate);
    }

    render() {
        return (
                <div className="fit">
                    <h1>{this.props.name}</h1>
                    <div className="my-border">
                        <table>
                            <tbody>
                            <tr>
                                <th>Array preparation:</th>
                                <th>Array to sort</th>
                            </tr>
                            <tr>
                                <td>
                                    <Generation name="Array to sort generation" updateParent={this.updateParent} />
                                </td>
                                <td rowSpan="2"><textarea className="my-textarea my-border" value={this.state.textareaValue} onChange={this.updateValue}/></td>
                            </tr>
                            <tr>
                                <td>
                                    <Sorting updateParent={this.updateParent} textareaValue={this.state.textareaValue} />
                                </td>
                            </tr>
                            </tbody>
                        </table>
                    </div>
                </div>
        );
    }
}

export default GeneralSorting;
