import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import * as serviceWorker from './serviceWorker';

class Car extends React.Component {
    kotes = {
        "Ford": {
            auto: { 
                brand: "Ford",
                model: "Mustang",
                color: "red",
                year: 1964
            }, 
            style: {
                color: "white",
                backgroundColor: "red"
            }
        },
        "Kot": {
            auto: { 
                brand: "Kot",
                model: "Kotang",
                color: "blue",
                year: 1990
            }, 
            style: {
                color: "yellow",
                backgroundColor: "blue"
            }
        }
    };

    constructor() {
        super();
        this.state = this.kotes["Ford"];
    }

    changeColor() {
        this.setState(this.kotes[this.state.auto.brand === "Ford" ? "Kot" : "Ford"]);
    }

    render() {
        return (
            <div>
            <h1>My {this.state.auto.brand}</h1>
            <p>
                It is a <b style={this.state.style}>{this.state.auto.color}</b> {this.state.auto.model} from {this.state.auto.year}.
            </p>
            <button
                type="button"
                onClick={this.changeColor.bind(this)}
            >Change color</button>
            </div>
        );
    }
  }

class Garage extends React.Component {
    render() {
      return (
        <div>
        <h1>Who lives in my garage?</h1>
        <Car />
        </div>
      );
    }
}

ReactDOM.render(<Garage />, document.getElementById('root'));

serviceWorker.unregister();
