import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import * as serviceWorker from './serviceWorker';

class Car extends React.Component {
    constructor() {
        super();
        this.state = {auto: { text: 'Not clicked!' }, style: {color: "white", backgroundColor: "darkGreen"}};
    }

    onButtonClick() {
        var state = this.state;
        var style = this.state.style;
        state.auto.text = this.state.auto.text === "Clicked!" ? "Not clicked!" : "Clicked!";
        state.style = this.state.style.color === "white" ? {color: "yellow", backgroundColor: "blue"} : {color: "white", backgroundColor: "darkGreen"};
        this.setState(state);
    }

    render() {
        return (
          <div className="main">
            <p style={this.state.style}>{this.state.auto.text}</p>
            <button onClick={this.onButtonClick.bind(this)}>Click</button>
          </div>
        );
    }
  }

class Garage extends React.Component {
    render() {
      return (
        <div>
        <h1>Who lives in my garage?</h1>
        <Car />
        </div>
      );
    }
}

ReactDOM.render(<Garage />, document.getElementById('root'));

serviceWorker.unregister();
