import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import * as serviceWorker from './serviceWorker';

class Car extends React.Component {
    constructor() {
        super();
        this.state = {
                auto: { 
                    text: 'Not clicked!',
                    brand: "Ford",
                    model: "Mustang",
                    color: "red",
                    year: 1964
                }, 
                style: {
                    color: "white",
                    backgroundColor: "red"
                }
            };
    }

    changeColor() {
        var brand = this.state.auto.brand;
        this.state.auto.brand = brand === "Ford" ? "Kot" : "Ford";
        this.state.auto.model = brand === "Ford" ? "Kotang" : "Mustang";
        this.state.auto.color = brand === "Ford" ? "blue" : "red";
        this.state.auto.year = brand === "Ford" ? 1990 : 1964;

        this.state.style = brand === "Ford" ? {color: "yellow", backgroundColor: "blue"} : {color: "white", backgroundColor: "red"};
        this.setState(this.state);
    }

    render() {
        return (
            <div>
            <h1>My {this.state.auto.brand}</h1>
            <p>
                It is a <b style={this.state.style}>{this.state.auto.color}</b> {this.state.auto.model} from {this.state.auto.year}.
            </p>
            <button
                type="button"
                onClick={this.changeColor.bind(this)}
            >Change color</button>
            </div>
        );
    }
  }

class Garage extends React.Component {
    render() {
      return (
        <div>
        <h1>Who lives in my garage?</h1>
        <Car />
        </div>
      );
    }
}

ReactDOM.render(<Garage />, document.getElementById('root'));

serviceWorker.unregister();
