import { Component } from 'react';
import Cookie from 'react-cookie';

class Block extends Component {
    waitStyle = {};
    waitSize = 60;
    waitTop = 0;
    waitLeft = 265;
    baseTop = 0;
    token = undefined;

    rightAlign = {textAlign: "right"};
    fitContent = {width: "fit-content"};
    border = {border: "1px solid black"};
    bold = {fontWeight: "bold"};

    constructor(props) {
        super(props);
        if (Cookie.get("token") !== undefined) {
            this.token = Cookie.get("token");
        }
    }

    setStateParam(param, value) {
        var state = this.state;
        state[param] = value;
        this.setState(state);
    }

    getStateParam(param) {
        return this.state[param];
    }

    setStateCookieParam(param, value, maxAge) {
        this.setStateParam(param, value);
        Cookies.set(param, value, {maxAge: maxAge});
    }

    getCookieParam(param) {
        return Cookies.get(param);
    }

    calculateWaitStyle() {
        this.waitStyle = this.state.isButtonClicked 
            ? {
                "position": "absolute",
                "top": this.waitTop + "px", 
                "left": this.waitLeft + "px", 
                "width": this.waitSize + "px", 
                "height": this.waitSize + "px", 
                "display": "block"
            } 
            : {
                "display": "none"
            };
    }
}

export default Block;
