import { Component } from 'react';

class Block  extends Component {
    mystyle = {
        textAlign: "right"
    };

    waitSize = 40;
    waitTop = 0;
    waitLeft = 0;

    calculateWaitPos(event) {
        this.waitTop = event.clientY + (event.currentTarget.offsetHeight - waitSize) / 2;
        this.waitLeft = event.clientX + (event.currentTarget.offsetWidth - waitSize);
    }

    setStateParam(param, value) {
        var state = this.state;
        state[param] = value;
        this.setState(state);
    }
}

export default Block;
