import React from 'react';
import ReactDOM from "react-dom";
import "./GeneralSorting.css";
import Block from './utils/Block';
import Generation from './Generation';
import Sorting from './Sorting';

class GeneralSorting extends Block {
    constructor(props) {
        super(props);
        this.state = {
            textareaValue: ""
        };
        this.updateValue = this.updateValue.bind(this);
        this.updateParent = this.updateParent.bind(this);
        this.reference = React.createRef();
        this.offsetTop = 20;
    }

    updateValue(event) {
        this.setStateParam("textareaValue", event.target.value);
    }

    updateParent(value) {
        this.setStateParam("textareaValue", value);
    }

    showWaitIcon(offsetTop) {
        alert(offsetTop);
    }

    componentDidMount() {
        this.waitTop += this.reference.current.offsetTop;
    }

    render() {
        return (
                <div ref={this.reference} style={this.fitContent}>
                    <h1>{this.props.name} ({this.props.pos})</h1>
                    <div style={this.border}>
                        <table>
                            <tbody>
                            <tr>
                                <th>Array preparation:</th>
                                <th>Array to sort</th>
                            </tr>
                            <tr>
                                <td>
                                    <Generation name="Array to sort generation" updateParent={this.updateParent} showWaitIcon={this.showWaitIcon} />
                                </td>
                                <td rowSpan="2"><textarea value={this.state.textareaValue} onChange={this.updateValue}/></td>
                            </tr>
                            <tr>
                                <td>
                                    <Sorting updateParent={this.updateParent} textareaValue={this.state.textareaValue} sort={this.props.sort} showWaitIcon={this.showWaitIcon} />
                                </td>
                            </tr>
                            </tbody>
                        </table>
                    </div>
                </div>
        );
    }
}

export default GeneralSorting;
