import Axios from 'axios';
import Block from './utils/Block';
import React from 'react';
import "./GeneralSorting.css";
import { properties } from './../env.properties';


class Autorization extends Block {
    constructor(props) {
        super(props);
        this.reference = React.createRef();
        this.userName = "user";
        this.password = "password";
        this.token = undefined;
    }

    userNameChange(event) {
        this.setStateParam("userName", event.target.value);
    }

    passwordChange(event) {
        this.setStateParam("password", event.target.value);
    }

    handleUnauthButton(event) {
    }

    handleAuthButton(event) {
        Axios.post(properties.tokenPath, "username=" + this.userName + "&password=" + this.password)
            .then(response => {
                this.token = response.data;
                alert(this.token);
            })
            .catch(error => {
                this.token = undefined;
            });
    }

    renderAutorized() {
        return (
            <div ref={this.reference} style={this.border}>
                <div style={this.bold}>Autorization</div>
                <div>Welcome, <b>{this.userName}</b></div>
                <button onClick={this.handleAuthhandleUnauthButtonButton = this.handleUnauthButton.bind(this)}>Logout</button>
            </div>
        );
    }

    renderUnautorized() {
        return (
            <div ref={this.reference} style={this.border}>
                <div style={this.bold}>Autorization</div>
                <div style={this.rightAlign}>
                    <label> 
                        User name:
                        <input type="text" value={this.userName} onChange={this.userNameChange = this.userNameChange.bind(this)} />
                    </label>
                </div>
                <div style={this.rightAlign}>
                    <label> 
                        Password:
                        <input type="password" value={this.password} onChange={this.passwordChange = this.passwordChange.bind(this)} />
                    </label>
                </div>
                <button onClick={this.handleAuthButton = this.handleAuthButton.bind(this)}>Log in</button>
            </div>
        );
    }

    render() {
        return this.token === undefined ? this.renderUnautorized() : this.renderAutorized();
    }
}

export default Autorization;
