import React, { Component } from 'react';
import "./GeneralSorting.css";
import "./../env.properties";
import Axios from 'axios';
import { properties } from './../env.properties';

class Generation extends Component {
    mystyle = {
        textAlign: "right"
    };

    constructor(props) {
        super(props);
        this.state = {
            options: []
        };
    }
    
    componentDidMount() {
        let opts = this.readSortOptions();
        this.setState({
            options: opts
        });
    }

    readSortOptions() {
        let opts = ['AFG', 'ALA', 'ALB'];
        Axios.get(properties.appPath + "/enum_s")
            .then(response => opts = response.data)
            .catch(error => opts = ['EEE', 'WWW']);
        return opts;
    }

    render() {
        const { options } = this.state;

        let optionsList = options.map((item, i) => {
          return (
            <option key={i} value={item}>{item}</option>
          )
        }, this);

        return (
            <div className="my-border">
                <div>
                    <label>
                        Sort type:
                        <select name="sortEnum">
                            {optionsList}
                        </select>
                    </label>
                </div>
                <div>
                    <label> 
                        Ascending:
                        <input type="checkbox" />
                    </label>
                </div>
                <button className="my-button">Generate</button>
            </div>
        );
    }
}

export default Generation;
