import React from 'react';
import Axios from 'axios';
import Block from "./utils/Block"
import "./GeneralSorting.css";
import waitIcon from "./../assets/img/wait.gif";
import { properties } from './../env.properties';

class Generation extends Block {
    constructor(props) {
        super(props);
        this.waitTop = 135;
        this.reference = React.createRef();
        this.state = {
            avoidDuplicates: true,
            n: 10,
            maxValue: 10,
            isButtonClicked: false
        };
    }

    handleAvoidDuplicatesChange(event) {
        this.setStateParam("avoidDuplicates", event.target.checked);
    }

    handleNChange(event) {
        this.setStateParam("n", event.target.value);
    }
    
    handleMaxValueChange(event) {
        this.setStateParam("maxValue", event.target.value);
    }

    checkInputParams() {
        var isOk = true;
        if (isNaN(this.state.n)) {
          isOk = false;
          alert("Number of items is not a number");
        } else if (this.state.n === 0 || this.state.n === "") {
          isOk = false;
          alert("Number of items equals zero or empty");
        } else if (isNaN(this.state.maxValue)) {
          isOk = false;
          alert("Max value is not a number");
        } else if (this.state.maxValue === 0 || this.state.maxValue === "") {
          isOk = false;
          alert("Max value equals zero or empty");
        } else if (this.state.avoidDuplicates && this.state.maxValue < this.state.n) {
          isOk = false;
          alert("Сannot avoid duplicates when max value less than number of items");
        }
        return isOk;
    }

    handleGenerateButton(event) {
        if (this.checkInputParams()) {
            var params = "?n=" + this.state.n 
                    + "&maxValue=" + this.state.maxValue 
                    + (this.state.avoidDuplicates ? "" : "&avoidDuplicates=false");
            this.setStateParam("isButtonClicked", true);
            Axios.get(properties.appPath + params)
                .then(response => {
                    this.props.updateParent("[" + response.data + "]");
                    this.setStateParam("isButtonClicked", false);
                })
                .catch(error => {
                    alert(error.message);
                    this.setStateParam("isButtonClicked", false);
                });
        }
    }

    componentDidMount() {
        alert(this.reference.current.offsetParent.offsetParent.offsetTop);
    }

    render() {
        this.calculateWaitStyle();
        return (
            <div ref={this.reference} style={this.border}>
                <div style={this.bold}>{this.props.name}</div>
                <div>
                    <label> 
                        Avoid duplicates:
                        <input type="checkbox" onChange={this.handleAvoidDuplicatesChange = this.handleAvoidDuplicatesChange.bind(this)} defaultChecked={this.state.avoidDuplicates} />
                    </label>
                </div>
                <div style={this.rightAlign}>
                    <label> 
                        Number of items:
                        <input type="text" onChange={this.handleNChange = this.handleNChange.bind(this)} value={this.state.n} />
                    </label>
                </div>
                <div style={this.rightAlign} >
                    <label> 
                        Max value:
                        <input type="text" onChange={this.handleMaxValueChange = this.handleMaxValueChange.bind(this)} value={this.state.maxValue} />
                    </label>
                </div>
                <button onClick={this.handleGenerateButton = this.handleGenerateButton.bind(this)}>
                    Generate
                </button>
                <img src={waitIcon} style={this.waitStyle} alt="Wait a bit, please..." />
            </div>
        );
    }
}

export default Generation;
