import React, { Component } from 'react';
import "./GeneralSorting.css";
import Generation from './Generation';
import Sorting from './Sorting';

class GeneralSorting extends Component {
    constructor(props) {
        super(props);
        this.state = {
            textareaValue: ""
        };
        this.updateTextareaValue = this.updateTextareaValue.bind(this);
    }

    updateTextareaValue(value) {
        this.setState({textareaValue: ""})
    }

    render() {
        return (
                <div className="fit">
                    <h1>{this.props.name}</h1>
                    <div className="my-border">
                        <table>
                            <tbody>
                            <tr>
                                <th>Array preparation:</th>
                                <th>Array to sort</th>
                            </tr>
                            <tr>
                                <td>
                                    <Generation name="Array to sort generation" />
                                </td>
                                <td rowSpan="2"><textarea className="my-textarea my-border" value="[2,9,8,6,4,3,0]" onChange={this.updateTextareaValue}/></td>
                            </tr>
                            <tr>
                                <td>
                                    <Sorting textareaValue={this.updateTextareaValue} />
                                </td>
                            </tr>
                            </tbody>
                        </table>
                    </div>
                </div>
        );
    }
}

export default GeneralSorting;
