import React from 'react';
import Block from './utils/Block';
import Autorization from './Autorization';
import Generation from './Generation';
import Sorting from './Sorting';
import waitIcon from "./../assets/img/wait.gif";
import "./GeneralSorting.css";

class GeneralSorting extends Block {
    constructor(props) {
        super(props);
        this.state = {
            textareaValue: ""
        };
        this.updateValue = this.updateValue.bind(this);
        this.updateParent = this.updateParent.bind(this);
        this.setWaitPos = this.setWaitPos.bind(this);
        this.getStateParam = this.getStateParam.bind(this);
        this.setStateParam = this.setStateParam.bind(this);
        
        this.reference = React.createRef();
    }

    updateValue(event) {
        this.setStateParam("textareaValue", event.target.value);
    }

    updateParent(value) {
        this.setStateParam("textareaValue", value);
    }

    setWaitPos(valueTop, valueLeft) {
        this.waitTop = this.reference.current.offsetTop + valueTop;
        this.waitLeft = this.reference.current.offsetLeft + valueLeft;
    }

    render() {
        this.calculateWaitStyle();
        return (
                <div ref={this.reference} style={this.fitContent}>
                    <h1>{this.props.name}</h1>
                    <div style={this.border}>
                        <table>
                            <tbody>
                            <tr>
                                <th>Array preparation:</th>
                                <th>Array to sort</th>
                            </tr>
                            <tr>
                                <td>
                                <Autorization 
                                        getStateParam={this.getStateParam} 
                                        setStateParam={this.setStateParam} 
                                        setWaitPos={this.setWaitPos}/>
                                </td>
                                <td rowSpan="3"><textarea value={this.state.textareaValue} onChange={this.updateValue}/></td>
                            </tr>
                            <tr>
                                <td>
                                    <Generation 
                                        name="Array to sort generation" 
                                        updateParent={this.updateParent} 
                                        getStateParam={this.getStateParam} 
                                        setStateParam={this.setStateParam} 
                                        setWaitPos={this.setWaitPos}/>
                                </td>
                            </tr>
                            <tr>
                                <td>
                                    <Sorting 
                                        updateParent={this.updateParent} 
                                        textareaValue={this.state.textareaValue} 
                                        sort={this.props.sort} 
                                        showWaitIcon={this.showWaitIcon} 
                                        getStateParam={this.getStateParam} 
                                        setStateParam={this.setStateParam} 
                                        setWaitPos={this.setWaitPos}/>
                                </td>
                            </tr>
                            </tbody>
                        </table>
                        <img src={waitIcon} style={this.waitStyle} alt="Wait a bit, please..." />
                    </div>
                </div>
        );
    }
}

export default GeneralSorting;
