import { Component } from 'react';

class Block  extends Component {
    waitStyle = {};
    waitSize = 40;
    waitTop = 0;
    waitLeft = 280;

    mystyle = {
        textAlign: "right"
    };
    
    setStateParam(param, value) {
        var state = this.state;
        state[param] = value;
        this.setState(state);
    }

    calculateWaitStyle() {
        this.waitStyle = this.state.isButtonClicked 
            ? {
                "position": "absolute",
                "display": "block",
                "top": this.waitTop + "px", 
                "left": this.waitLeft + "px", 
                "width": this.waitSize + "px", 
                "height": this.waitSize + "px", 
                "display": "block"
            } 
            : {
                "display": "none"
            };
    }
}

export default Block;
