import React from 'react';
import Axios from 'axios';
import Block from "./utils/Block"
import "./GeneralSorting.css";
import waitIcon from "./../assets/img/wait.gif";
import { properties } from './../env.properties';

class Sorting extends Block {
    constructor(props) {
        super(props);
        this.reference = React.createRef();
        this.waitTop = 35;
        this.state = {
            options: [],
            sort: this.props.sort === "" || this.props.sort === undefined ? "QUICK" : this.props.sort.toUpperCase(),
            asc: true,
            isButtonClicked: false
        };
    }

    handleSortChange(event) {
        this.setStateParam("sort", event.target.value);
    }
    
    handleAscChange(event) {
        this.setStateParam("asc", event.target.checked);
    }
    
    handleSortButton(event) {
        var params = "?sort=" + this.state.sort + (this.state.asc ? "" : "&asc=false");
        var items = this.props.textareaValue.replace("[", "").replace("]", "").split(",");

        this.setStateParam("isButtonClicked", true);
        Axios.post(properties.appPath + params, items)
            .then(response => {
                this.props.updateParent("[" + response.data.sortedItems + "]");
                this.setStateParam("isButtonClicked", false);
            })
            .catch(error => {
                alert(error.response.message);
                this.setStateParam("isButtonClicked", false);
            });
    }
    
    componentDidMount() {
        alert("eee");
        this.waitTop += this.reference.current.offsetParent.offsetParent.offsetTop;
        Axios.get(properties.appPath + "/enums")
            .then(response => {
                this.setStateParam("options", response.data);
            })
            .catch(error => {
                this.setStateParam("options", "-- ERROR --");
            });
    }

    render() {
        this.calculateWaitStyle();
        let optionsList = this.state.options.map((item, i) => {
          return (
            <option key={i} value={item} >{item}</option>
          )
        }, this);
        return (
            <div ref={this.reference} style={this.border}>
                <div>
                    <label>
                        Sort type:&nbsp;
                        <select name="sortEnum" value={this.state.sort} onChange={this.handleSortChange = this.handleSortChange.bind(this)}>
                            {optionsList}
                        </select>
                    </label>
                </div>
                <div>
                    <label> 
                        Ascending:&nbsp;
                        <input type="checkbox" onChange={this.handleAscChange = this.handleAscChange.bind(this)} defaultChecked={this.state.asc} />
                    </label>
                </div>
                <button onClick={this.handleSortButton = this.handleSortButton.bind(this)}>Sort</button>
                <img src={waitIcon} style={this.waitStyle} alt="Wait a bit, please..." />
            </div>
        );
    }
}

export default Sorting;
