import { Component } from 'react';

class Block  extends Component {
    mystyle = {
        textAlign: "right"
    };

    waitTop = 0;
    waitLeft = 0;
    
    setStateParam(param, value) {
        var state = this.state;
        state[param] = value;
        this.setState(state);
    }
}

export default Block;
