import React from 'react';
import "./GeneralSorting.css";
import Axios from 'axios';
import Block from "./utils/Block"

import waitIcon from "./../assets/img/wait.gif";
import { properties } from './../env.properties';

class Sorting extends Block {
    waitTop = 350;

    constructor(props) {
        super(props);
        this.state = {
            options: [],
            sort: "QUICK",
            asc: true,
            isSorting: false
        };
        this.handleSortChange = this.handleSortChange.bind(this);
        this.handleAscChange = this.handleAscChange.bind(this);
        this.handleSortButton = this.handleSortButton.bind(this);
    }

    handleSortChange(event) {
        this.setStateParam("sort", event.target.value);
    }
    
    handleAscChange(event) {
        this.setStateParam("asc", event.target.checked);
    }
    
    handleSortButton(event) {
        var params = "?sort=" + this.state.sort + (this.state.asc ? "" : "&asc=false");
        var items = this.props.textareaValue.replace("[", "").replace("]", "").split(",");

        this.setStateParam("isSorting", true);
        Axios.post(properties.appPath + params, items)
            .then(response => this.props.updateParent("[" + response.data.sortedItems + "]"))
            .catch(error => alert(error.response.message));
    }
    
    componentDidMount() {
        Axios.get(properties.appPath + "/enums")
            .then(response => {
                this.setStateParam("options", response.data);
            })
            .catch(error => {
                this.setStateParam("options", "-- ERROR --");
            });
    }

    render() {
        var style = this.state.isSorting ? {"top": this.waitTop + "px", "display": "block"} : {};
        let optionsList = this.state.options.map((item, i) => {
          return (
            <option key={i} value={item} >{item}</option>
          )
        }, this);
        return (
            <div className="my-border">
                <div>
                    <label>
                        Sort type:&nbsp;
                        <select name="sortEnum" value={this.state.sort} onChange={this.handleSortChange}>
                            {optionsList}
                        </select>
                    </label>
                </div>
                <div>
                    <label> 
                        Ascending:&nbsp;
                        <input type="checkbox" onChange={this.handleAscChange} defaultChecked={this.state.asc} />
                    </label>
                </div>
                <button className="my-button" onClick={this.handleSortButton}>Sort</button>
                <img src={waitIcon} className="waitStyle" style={style} alt="Wait a bit, please..." />
            </div>
        );
    }
}

export default Sorting;
