import { Component } from 'react';

class Block  extends Component {
    waitStyle = {};
    waitSize = 60;
    waitTop = 0;
    waitLeft = 265;
    baseTop = 0;

    rightAlign = {textAlign: "right"};
    fitContent = {width: "fit-content"};
    border = {border: "1px solid black"};
    bold = {fontWeight: "bold"};
    
    setStateParam(param, value) {
        var state = this.state;
        state[param] = value;
        this.setState(state);
    }

    calculateWaitStyle() {
        this.waitStyle = this.state.isButtonClicked 
            ? {
                "position": "absolute",
                "top": this.waitTop + "px", 
                "left": this.waitLeft + "px", 
                "width": this.waitSize + "px", 
                "height": this.waitSize + "px", 
                "display": "block"
            } 
            : {
                "display": "none"
            };
    }
}

export default Block;
