import Axios from 'axios';
import Block from './utils/Block';
import React from 'react';
import "./GeneralSorting.css";
import cookie from "react-cookies";
import { properties } from './../env.properties';


class Autorization extends Block {
    constructor(props) {
        super(props);
        this.reference = React.createRef();
        this.state.userName = cookie.load("userName");
    }

    userNameChange(event) {
        this.state.userName = event.target.value;
    }

    passwordChange(event) {
        this.state.password = event.target.value;
    }

    handleUnauthButton(event) {
        this.props.setStateParam("token", undefined);
        cookie.remove("token");
    }

    handleAuthButton(event) {
        cookie.save("userName", this.state.userName, {maxAge: 600});
        Axios.post(properties.tokenPath, "username=" + this.state.userName + "&password=" + this.state.password)
            .then(response => {
                this.props.setStateParam("token", response.data);
                cookie.save("token", response.data, {maxAge: 700});
            })
            .catch(error => {
                this.props.setStateParam("token", undefined);
                cookie.remove("token");
            });
        this.state.password = undefined;
    }

    renderAutorized() {
        return (
            <div ref={this.reference} style={this.border}>
                <div style={this.bold}>Autorization</div>
                <div>Welcome, <b>{this.state.userName}</b></div>
                <button onClick={this.handleUnauthButton=this.handleUnauthButton.bind(this)}>Logout</button>
            </div>
        );
    }

    renderUnautorized() {
        return (
            <div ref={this.reference} style={this.border}>
                <div style={this.bold}>Autorization</div>
                <div style={this.rightAlign}>
                    <label> 
                        User name:
                        <input type="text" value={this.state.userName} onChange={this.userNameChange=this.userNameChange.bind(this)} />
                    </label>
                </div>
                <div style={this.rightAlign}>
                    <label> 
                        Password:
                        <input type="password" value={this.state.password} onChange={this.passwordChange=this.passwordChange.bind(this)} />
                    </label>
                </div>
                <button onClick={this.handleAuthButton=this.handleAuthButton.bind(this)}>Log in</button>
            </div>
        );
    }

    render() {
        return this.props.getStateParam("token") === undefined ? this.renderUnautorized() : this.renderAutorized();
    }
}

export default Autorization;
