import React, { Component } from 'react';
import "./GeneralSorting.css";
import Generation from './Generation';
import Sorting from './Sorting';

class GeneralSorting extends Component {
    constructor(props) {
        super(props);
        this.state = {
            textareaValue: "[2,9,8,6,4,3,0]"
        };
        this.updateValue = this.updateValue.bind(this);
    }

    updateValue(event) {
        var mystate = this.state;
        mystate.textareaValue = event.target.value;

        this.setState(mystate);
    }

    updateFromChild(value) {
        var mystate = this.state;
        mystate.textareaValue = value;

        this.setState(mystate);
    }

    render() {
        return (
                <div className="fit">
                    <h1>{this.props.name}</h1>
                    <div className="my-border">
                        <table>
                            <tbody>
                            <tr>
                                <th>Array preparation:</th>
                                <th>Array to sort</th>
                            </tr>
                            <tr>
                                <td>
                                    <Generation name="Array to sort generation" />
                                </td>
                                <td rowSpan="2"><textarea className="my-textarea my-border" value={this.state.textareaValue} onChange={this.updateValue}/></td>
                            </tr>
                            <tr>
                                <td>
                                    <Sorting updateParent={this.updateFromChild} textareaValue={this.state.textareaValue} />
                                </td>
                            </tr>
                            </tbody>
                        </table>
                    </div>
                </div>
        );
    }
}

export default GeneralSorting;
