import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import * as serviceWorker from './serviceWorker';

class Car extends React.Component {
    auto = {
        brand: "Ford",
        model: "Mustang",
        color: "red",
        year: 1964
    };

    mystyle = {
        color: "white",
        backgroundColor: "red"
    };

    changeColor() {
        alert(this.mystyle.color + "\n" + this.mystyle.backgroundColor + "\n" + this.auto.color);
        this.auto.color = this.auto.color === "red" ? "blue" : "red";
        this.mystyle = {
            color: this.auto.color === "red" ? "white" : "yellow",
            backgroundColor: this.auto.color
        };
    }

    render() {
        return (
            <div>
            <h1>My {this.auto.brand}</h1>
            <p>
                It is a <b style={this.mystyle}>{this.auto.color}</b> {this.auto.model} from {this.auto.year}.
            </p>
            <button
                type="button"
                onClick={() => this.changeColor}
            >Change color</button>
            </div>
      );
    }
  }

class Garage extends React.Component {
    render() {
      return (
        <div>
        <h1>Who lives in my garage?</h1>
        <Car brand="Ford" />
        </div>
      );
    }
}

ReactDOM.render(<Garage />, document.getElementById('root'));

serviceWorker.unregister();
