import React, { Component } from 'react';
import "./GeneralSorting.css";
import "./../env.properties";
import Axios from 'axios';
import { properties } from './../env.properties';

class Generation extends Component {
    mystyle = {
        textAlign: "right"
    };

    constructor(props) {
        super(props);
        this.state = {
            options: [],
            sort: "QUICK"
        };
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(event) {
        mystate = this.state;
        mystate.sort = event.target.value;
        this.setState(mystate);
        alert(mystate);
    }
    
    componentDidMount() {
        Axios.get(properties.appPath + "/enums")
            .then(response => this.setState({options: response.data}))
            .catch(error => this.setState({options: ['-- ERROR ---']}));
    }

    render() {
        let optionsList = this.state.options.map((item, i) => {
          return (
            <option key={i} value={item} >{item}</option>
          )
        }, this);

        return (
            <div className="my-border">
                <div>
                    <label>
                        Sort type:&nbsp;
                        <select name="sortEnum" value={this.state.sort} onChange={this.handleChange}>
                            {optionsList}
                        </select>
                    </label>
                </div>
                <div>
                    <label> 
                        Ascending:&nbsp;
                        <input type="checkbox" />
                    </label>
                </div>
                <button className="my-button">Generate</button>
            </div>
        );
    }
}

export default Generation;
