import React, { Component } from 'react';
import "./GeneralSorting.css";
import "./../env.properties";
import Axios from 'axios';
import { properties } from './../env.properties';

class Generation extends Component {
    mystyle = {
        textAlign: "right"
    };

    constructor(props) {
        super(props);
        this.state = {
            countries: [],
            colours: {}
        };
    }
    
    componentDidMount() {
        this.setState({
            countries: this.readSortOptions()
        });
    }

    readSortOptions() {
        return ['AFG', 'ALA', 'ALB'];
    }

    render() {
        const { countries } = this.state;

        let countriesList = countries.map((item, i) => {
          return (
            <option key={i} value={item}>{item}</option>
          )
        }, this);

        /*
        let sortOptions = Axios.get(properties.appPath + "/enum_s")
            .then(response => { 
                response.data.map((option, i) => {
                    return (
                        <option key="{i}" value="{option}">{option}</option>
                    );
                });
            })
            .catch(error => { 
                return (
                    <option key="-1" value="-1">-- OPTION --</option>
                );
            });
            */
        return (
            <div className="my-border">
                <div>
                    <label>
                        Sort type:
                        <select name="sortEnum">
                            {countriesList}
                        </select>
                    </label>
                </div>
                <div>
                    <label> 
                        Ascending:
                        <input type="checkbox" />
                    </label>
                </div>
                <button className="my-button">Generate</button>
            </div>
        );
    }
}

export default Generation;
