import { Component } from 'react';
import cookie from "react-cookies";

class Block extends Component {
    waitStyle = {};
    waitSize = 60;
    waitTop = 0;
    waitLeft = 265;
    baseTop = 0;
    token = undefined;

    rightAlign = {textAlign: "right"};
    fitContent = {width: "fit-content"};
    border = {border: "1px solid black"};
    bold = {fontWeight: "bold"};

    constructor(props) {
        super(props);
        this.state = {};
        this.token = cookie.load("token");
    }

    setStateParam(param, value) {
        this[param] = value;
        var state = this.state;
        state[param] = value;
        this.setState(state);
    }

    getStateParam(param) {
        return this.state[param];
    }

    expandCookies() {
        cookie.save("userName", cookie.load("userName"), {maxAge: properties.cookiesSec});
        cookie.save("token", cookie.load("token"), {maxAge: properties.cookiesSec});
}

    calculateWaitStyle() {
        this.waitStyle = this.state.isButtonClicked 
            ? {
                "position": "absolute",
                "top": this.waitTop + "px", 
                "left": this.waitLeft + "px", 
                "width": this.waitSize + "px", 
                "height": this.waitSize + "px", 
                "display": "block"
            } 
            : {
                "display": "none"
            };
    }
}

export default Block;
