import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import * as serviceWorker from './serviceWorker';

class Car extends React.Component {
    constructor() {
        super();
        this.auto = {
            brand: "Ford",
            model: "Mustang",
            color: "red",
            year: 1964
        };
        this.mystyle = {
            color: "white",
            backgroundColor: "red"
        };
    }

    changeColor() {
        this.auto.brand = this.auto.brand === "Ford" ? "Kot" : "Ford";
        this.auto.model = this.auto.model === "Mustang" ? "Kotang" : "Mustang";
        this.auto.color = this.auto.color === "red" ? "blue" : "red";
        this.auto.year = this.auto.color === 1964 ? 1990 : 1964;
        this.mystyle = {
            color: this.auto.color === "red" ? "white" : "yellow",
            backgroundColor: this.auto.color
        };
    }

    render() {
        return (
            <div>
            <h1>My {this.auto.brand}</h1>
            <p>
                It is a <b style={this.mystyle}>{this.auto.color}</b> {this.auto.model} from {this.auto.year}.
            </p>
            <button
                type="button"
                onClick={this.changeColor.bind(this)}
            >Change color</button>
            </div>
      );
    }
  }

class Garage extends React.Component {
    render() {
      return (
        <div>
        <h1>Who lives in my garage?</h1>
        <Car />
        </div>
      );
    }
}

ReactDOM.render(<Garage />, document.getElementById('root'));

serviceWorker.unregister();
