import React, { Component } from 'react';
import "./GeneralSorting.css";

class Generation extends Component {
    mystyle = {
        textAlign: "right"
    };

    constructor(props) {
        super(props);
        this.state = {
            avoidDuplicates: true,
            n: 10,
            maxValue: 10
        };
        this.handleAvoidDuplicatesChange = this.handleAvoidDuplicatesChange.bind(this);
        this.handleNChange = this.handleNChange.bind(this);
        this.handleMaxValueChange = this.handleMaxValueChange.bind(this);
        this.handleGenerateButton = this.handleGenerateButton.bind(this);
    }

    handleAvoidDuplicatesChange(event) {
        var mystate = this.state;
        mystate.avoidDuplicates = event.target.checked;

        this.setState(mystate);
    }

    handleNChange(event) {
        var mystate = this.state;
        mystate.n = event.target.value;

        this.setState(mystate);
    }
    
    handleMaxValueChange(event) {
        var mystate = this.state;
        mystate.maxValue = event.target.value;

        this.setState(mystate);
    }
    
    handleGenerateButton(event) {
        var params = "?n=" + this.state.n 
                + "&maxValue=" + this.state.maxValue 
                + (this.state.avoidDuplicates ? "" : "&asc=false");

        Axios.get(properties.appPath + params)
            .then(response => this.props.updateParent("[" + response.data.sortedItems + "]"))
            .catch(error => alert(error.response.message));
    }

    render() {
        return (
            <div className="my-border">
                <div className="my-bold">{this.props.name}</div>
                <div>
                    <label> 
                        Avoid duplicates:
                        <input type="checkbox" onChange={this.handleAvoidDuplicatesChange} defaultChecked={this.state.avoidDuplicates} />
                    </label>
                </div>
                <div style={this.mystyle}>
                    <label> 
                        Number of items:
                        <input type="text" onChange={this.handleNChange} value={this.state.n} />
                    </label>
                </div>
                <div style={this.mystyle} >
                    <label> 
                        Max value:
                        <input type="text" onChange={this.handleMaxValueChange} value={this.state.maxValue} />
                    </label>
                </div>
                <button className="my-button" onClick={this.handleGenerateButton}>Generate</button>
            </div>
        );
    }
}

export default Generation;
