import { Component } from 'react';
import waitIcon from "./../../assets/img/waitIcon.gif";

class Block  extends Component {
    mystyle = {
        textAlign: "right"
    };
    waitSize = 40;
    waitIcon;

    setStateParam(param, value) {
        var state = this.state;
        state[param] = value;
        this.setState(state);
    }
}

export default Block;
