import React, { Component } from 'react';
import "./GeneralSorting.css";
import "./../env.properties";
import Axios from 'axios';
import { properties } from './../env.properties';

class Generation extends Component {
    mystyle = {
        textAlign: "right"
    };

    options = [];

    constructor(props) {
        super(props);
    }
    
    componentDidMount() {
        Axios.get(properties.appPath + "/enums")
            .then(response => this.options = response.data)
            .catch(error => this.setState(this.options = ['-- ERROR ---']));
    }

    render() {
        let optionsList = this.options.map((item, i) => {
          return (
            <option key={i} value={item}>{item}</option>
          )
        }, this);

        return (
            <div className="my-border">
                <div>
                    <label>
                        Sort type:&nbsp;
                        <select name="sortEnum">
                            {optionsList}
                        </select>
                    </label>
                </div>
                <div>
                    <label> 
                        Ascending:&nbsp;
                        <input type="checkbox" />
                    </label>
                </div>
                <button className="my-button">Generate</button>
            </div>
        );
    }
}

export default Generation;
