import React from 'react';
import Axios from 'axios';
import Block from "./utils/Block"
import "./GeneralSorting.css";
import waitIcon from "./../assets/img/wait.gif";
import { properties } from './../env.properties';

class Generation extends Block {
    waitTop = 240;

    constructor(props) {
        super(props);
        this.state = {
            avoidDuplicates: true,
            n: 10,
            maxValue: 10,
            isGenerating: false
        };
        this.handleAvoidDuplicatesChange = this.handleAvoidDuplicatesChange.bind(this);
        this.handleNChange = this.handleNChange.bind(this);
        this.handleMaxValueChange = this.handleMaxValueChange.bind(this);
        this.handleGenerateButton = this.handleGenerateButton.bind(this);
        this.checkInputParams = this.checkInputParams.bind(this);
    }

    handleAvoidDuplicatesChange(event) {
        this.setStateParam("avoidDuplicates", event.target.checked);
    }

    handleNChange(event) {
        this.setStateParam("n", event.target.value);
    }
    
    handleMaxValueChange(event) {
        this.setStateParam("maxValue", event.target.value);
    }

    checkInputParams() {
        var isOk = true;
        if (isNaN(this.state.n)) {
          isOk = false;
          alert("Number of items is not a number");
        } else if (this.state.n === 0 || this.state.n === "") {
          isOk = false;
          alert("Number of items equals zero or empty");
        } else if (isNaN(this.state.maxValue)) {
          isOk = false;
          alert("Max value is not a number");
        } else if (this.state.maxValue === 0 || this.state.maxValue === "") {
          isOk = false;
          alert("Max value equals zero or empty");
        } else if (this.state.avoidDuplicates && this.state.maxValue < this.state.n) {
          isOk = false;
          alert("Сannot avoid duplicates when max value less than number of items");
        }
        return isOk;
    }

    handleGenerateButton(event) {
        if (this.checkInputParams()) {
            var params = "?n=" + this.state.n 
                    + "&maxValue=" + this.state.maxValue 
                    + (this.state.avoidDuplicates ? "" : "&avoidDuplicates=false");
            this.setStateParam("isGenerating", true);
            Axios.get(properties.appPath + params)
                .then(response => {
                    this.props.updateParent("[" + response.data + "]");
                    this.setStateParam("isGenerating", false);
                })
                .catch(error => {
                    alert(error.message);
                    this.setStateParam("isGenerating", false);
                });
        }
    }

    render() {
        var style = this.state.isGenerating ? {"top": this.waitTop + "px", "display": "block"} : {};
        return (
            <div className="my-border">
                <div className="my-bold">{this.props.name}</div>
                <div>
                    <label> 
                        Avoid duplicates:
                        <input type="checkbox" onChange={this.handleAvoidDuplicatesChange} defaultChecked={this.state.avoidDuplicates} />
                    </label>
                </div>
                <div style={this.mystyle}>
                    <label> 
                        Number of items:
                        <input type="text" onChange={this.handleNChange} value={this.state.n} />
                    </label>
                </div>
                <div style={this.mystyle} >
                    <label> 
                        Max value:
                        <input type="text" onChange={this.handleMaxValueChange} value={this.state.maxValue} />
                    </label>
                </div>
                <button className="my-button" onClick={this.handleGenerateButton}>
                    Generate
                </button>
                <img src={waitIcon} className="waitStyle" style={style} alt="Wait a bit, please..." />
            </div>
        );
    }
}

export default Generation;
