import { Component } from 'react';

class Block  extends Component {
    mystyle = {
        textAlign: "right"
    };

    waitStyle = {};
    waitSize = 40;
    waitTop = 0;
    waitLeft = 0;

    constructor(props) {
        super(props);
        this.calculateWaitStyle(false);
    }

    calculateWaitPos(event) {
        var indent = (event.currentTarget.offsetHeight - this.waitSize) / 2;
        this.waitTop = event.clientY + indent;
        this.waitLeft = event.clientX + (event.currentTarget.offsetWidth - this.waitSize - indent);
        this.calculateWaitStyle(true);
    }

    calculateWaitStyle(isClicked) {
        this.waitStyle = isClicked 
            ? {
                "position": "absolute",
                "width": this.waitSize + "px",
                "height": this.waitSize + "px",
                "top": this.waitTop + "px", 
                "left": this.waitLeft + "px", 
                "display": "block"
            } 
            : {
                "display": "none"
            };
    }

    setStateParam(param, value) {
        var state = this.state;
        state[param] = value;
        this.setState(state);
    }
}

export default Block;
